mapper.select:
        select id,name,img,seq,parentId,status,lastUpdateBy,lastUpdateTime
        from category
        
            
                and id=#{id}
            
            
                and id in
            
                #{item}
            
            
            
                and name like concat('%',#{name},'%')
            
            
                and parentId=#{parentId}
            
            
                and status=#{status}
